function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (newValue === '') {
      return;
   }
  var city = g_form.getValue('u_city');

	g_form.clearOptions('u_city');

	if ( newValue == 'australia' );{
	g_form.addOption('u_city','melborne','Melbourne');
	g_form.addOption('u_city','sydney','sydney');	
	}

	if ( newValue == 'canada');{
	g_form.addOption('u_city','toronto','toronto');
	g_form.addOption('u_city','vancouver','vancouver');	
	g_form.addOption('u_city','Quebec','Quebec');	

}	

		if ( newValue == 'india');{
	g_form.addOption('u_city','Delhi','delhi');
	g_form.addOption('u_city','bangalore','bangalore');	
	g_form.addOption('u_city','hyderabad','Hyderabad');	
	g_form.addOption('u_city','Chennai','Chennai');	

}

		if ( newValue == 'Mexico');{
	g_form.addOption('u_city','mexico city','mexico city');
	g_form.addOption('u_city','Tijuana','Tijuana');	
	g_form.addOption('u_city','Ecatapec','Ecatapec');	
	g_form.addOption('u_city','Chennai','Chennai');	
	g_form.addOption('u_city','hyderabad','Hyderabad');

}
			if ( newValue == 'United KIngdom');{
	g_form.addOption('u_city','Leeds','Leeds');
	g_form.addOption('u_city','Liver pool','Liver pool');	
	g_form.addOption('u_city','London','London');	
	g_form.addOption('u_city','Macnhester city','Manchester city');	

}

			if ( newValue == 'United States');{
	g_form.addOption('u_city','Los angeles','Los angeles');
	g_form.addOption('u_city','California','California');	
	g_form.addOption('u_city','New York','New york');	
	g_form.addOption('u_city','Chicago','Chicago');	
	g_form.addOption('u_city','New Jersey','new jersey');	
	g_form.addOption('u_city','Tampa Bay','Tampa bay');	
	g_form.addOption('u_city','Las Vegas','Las Vegas');	
}

}
