sysauto_script.script:
var howLongToClose = gs.getProperty('u_practise_table_user.autoCloseOverdue');

 // Query the database for NeedIt Task records with Due date field
 // values older the number of days set in the x_58872_needit.autoCloseOverdue
 // Application Property.  Only return NeedIt Task records have a State value of
 // Open or Work in progress.  Close the NeedIt Tasks and update
 // the NeedIt Task Work notes.
 var overdueNITasks = new GlideRecord('u_practise_table');
 overduePRATasks.addQuery('due_date','<=',gs.daysAgo(howLongToClose));
 overduePRATasks.addQuery('state','<',3);

 overduePRATasks.query();
 // Write a log message for each long overdue NeedIt Task Record.  
 // Add a note to the Work notes field.  Change the State to Closed Incomplete.
 // Update the NeedIt Task record. 
 while(overduePRATasks.next()){
   gs.info("Auto close practise Task record = " + overduePRATasks.number);
   overduePRATasks.work_notes = "Record closed because it was too far past the Due date.";
   overduePRATasks.state = 4;
   overduePRATasks.update();
 }
